# Random a interger from 1 to 4 at '.' and '*' in maze for UCS
for i in range(len(maze_grid)):
    for j in range(len(maze_grid[i])):
        if maze_grid[i][j] == '.' or maze_grid[i][j] == '*':
            maze_grid[i][j] = random.randint(1, 4)

# print the maze like the format of the grid with ' ' between each element and , and [ ] at the start and end of each row
for i in range(len(maze_grid)):
    print("[", end='')
    for j in range(len(maze_grid[i])):
        print( "'"+ str(maze_grid[i][j]) + "'", end=',')
    print("]", end='\n')